import React from 'react'
import ReactDom from 'react-dom'

// CSS
import './index.css'
// setup vars
const books = [
  {
    id: 1,
    img: 'https://m.media-amazon.com/images/I/91hIsjFuICL._AC_UL640_FMwebp_QL65_.jpg',
    title: 'I Love You to the Moon and Back',
    author: 'Amelia Hepworth',
  },
  {
    id: 2,
    img: 'https://m.media-amazon.com/images/I/71aLultW5EL._AC_UY436_FMwebp_QL65_.jpg',
    title: 'Our Class is a Family',
    author: 'Shannon Olsen',
  },
  {
    id: 3,
    img: 'https://m.media-amazon.com/images/I/71qWVhqYuOL._AC_UY436_FMwebp_QL65_.jpg',
    title: 'The Vanishing Half: A Novel',
    author: 'Brit Bennet',
  },
]

function BookList() {
  return (
    <section className='booklist'>
      {books.map((book, index) => {
        return <Book key={book.id} {...book}></Book>
      })}
    </section>
  )
}
const Book = ({ img, title, author }) => {
  // attribute, eventHandler
  // onClick, onMouseOver
  const clickHandler = () => {
    alert('hello world')
  }
  return (
    <article className='book'>
      <img src={img} alt='' />
      <h1>{title}</h1>
      <h4>{author}</h4>
      <button type='button' onClick={clickHandler}></button>
    </article>
  )
}
ReactDom.render(<BookList />, document.getElementById('root'))
